export default {
  __name: 'RobotGameTimer',
  setup(__props, {
    expose
  }) {
    expose();
    let interval = -1000; // ms
    let expected = Date.now() + interval;
    function step(seconds) {
      let dt = Date.now() - expected;
      if (dt < interval) {
        console.log("problem");
      }
      seconds += interval / 1000;
      setTimeout(seconds => {
        step(seconds);
      }, Math.max(0, interval - dt));
    }
    onMounted(() => {
      setTimeout(() => {
        step(150);
      }, interval);
    });
    const __returned__ = {
      get interval() {
        return interval;
      },
      set interval(v) {
        interval = v;
      },
      get expected() {
        return expected;
      },
      set expected(v) {
        expected = v;
      },
      step,
      onMounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
